	$(function(){
		$(window).bind("resize", function(){ jsMediaQueries(); calendarListView(); });

		head.ready("rsMainMenu", function(){
			// ADD GLOBAL ICONS INTO MENU
			var globalIcons = [
				['Parent Portal', 'https://campus.dasd.org/campus/portal/parents/downingtown.jsp'],
				['Food Services', 'https://www.dasd.org/Page/2664'],
				['District Calendar', 'https://www.dasd.org/Page/2'],
				['Employment', 'https://www.dasd.org/Page/2552'],
                ['Safety & Wellness', 'https://www.dasd.org/Page/9015']
			];
			var iconNum = ('5' == "5;4;3;2;1;0") ? 0 : '5';
			var globalLinksHeader = "", globalLinks = "";

			for(var i = 0; i < iconNum; i++) {
				var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
				globalLinks += "1, " + globalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(iconNum > 0) {
				globalLinksHeader = "Global Links";
			}


			// ADD GLOBAL TABS
			// GET GLOBAL TAB NUMBER
			var tabNum = ('4' == "5;4;3;2;1;0") ? 5 : '4';
			var tabs = "";

			// IF WE HAVE TABS TO BUILD
			if(tabNum > 0) {
				// DECLARE GLOBAL TABS ARRAY WITH DATA
				var tabsData = [
					["Students", "/Page/2407"],
					["Parents", "/Page/4874"],
					["Community ", "/Page/2409"],
					["Staff", "/Page/2408"],
					["Safety & Wellness", "/Page/9015"]
				];

				// BUILD OUT THE GLOBAL TABS
				for(var i = 0; i < tabNum; i++) {
					var siteCalendarLink = "/Page/2";
					var tabHref = tabsData[i][1];
					tabHref = tabHref.replace("[SITECALENDARLINK]", siteCalendarLink);

					if(iconNum > 0) {
						globalLinks += "2, " + tabsData[i][0] + ", " + tabHref + ", _self;";
					} else {
						globalLinks += "1, " + tabsData[i][0] + ", " + tabHref + ", _self;";
					}
				}
			}

			if(tabNum > 0) {
				if(iconNum > 0) {
					globalLinksHeader += "; Site Shortcuts";
				} else {
					globalLinksHeader += "Site Shortcuts";
				}
			}

			// RESPONSIVE MENU
			$("body").creativeResponsiveMenu({
				"breakPoint"		: 1023, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
				"folderURL"		: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-menu/rs-menu-files/",
				"useDefaultCSS"		: "yes",
				"slideDirection"		: "top-to-bottom", // OTHER OPTION IS 'left-to-right'
				"menuPosition"		: "absolute", // OTHER OPTION IS 'relative'
				"menuButtonParent"	: ".gb-mystart.left",
				"showMenuBtnText"	: "yes",
				"menuBtnText"		: "MENU",
				"showMenuInfoText"	: "no",
				"menuInfoText768"	: "",
				"menuInfoText640"	: "",
				"menuInfoText480"	: "",
				"menuInfoText320"	: "",
				"showSearch"		: "yes",
				"showMyAccount"		: "yes",
				"showSiteManager"       : "yes",
				"showDistrictHome"	: "yes",
				"showSchoolList"		: "yes",
				"showChannelList"	: "yes",
				"showTranslate"		: "no",
				"translateId"           : "none",
				"extraAccountItems"	: [], // ARRAY OF BUTTON IDS OR CLASSES
				"extraMenuOptions"	: globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
				"extraMenuItems"		: globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
				"siteID"		: "4"
			});
		});

		head.ready("rsPagelistMenu", function() {
			$("#sp-content").creativePagelistMenu({
				"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
				"folderURL"		: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-pagelist-menu/rs-menu-files/",
				"insertionMethod"	: "prepend", // OPTIONS ARE prepend OR append
				"menuBtnText"		: "Pages",
				"useDefaultCSS"		: "yes"
			});
		});

		jsMediaQueries();
		photoGallery();
		useChannelForSection();
		dynamicContent();
		buildStyleSheet();
		globalIcons();
		globalTabs();
		modEvents();
		gbSearch();
		if(!$(".ie8").length){
			initLogoBgCanvas();
			initSlideshowCurveOneCanvas();
			initSlideshowCurveOTwoCanvas();
			initSlideshowCurveOThreeCanvas();
			initChannelBarCornerCanvas();
		}
        SLM();
	});	

	function jsMediaQueries(){
		var getWidth = jsBreakPoint();
		if (getWidth == 960) { // 960
			$(window).scroll(parallaxScroll);
		} if (getWidth == 768) { // 768
			$(window).unbind("scroll", parallaxScroll);
			parallaxScrollReset();
			appView();
		} if (getWidth == 640) { // 640
			appView();
		} if (getWidth == 480) { // 480
			appView();
		} if (getWidth == 320) { // 320
			appView();
		}
	}

	function photoGallery(){
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
			        // PHOTO GALLERY ROTATOR
			        $("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
			                "imageWidth"                : 960,
			                "imageHeight"               : 505,
			                "appHeader"                 : "no",
			                "titles"                    : "yes",
			                "captions"                  : "yes",
			                "playPause"                 : "no",
