			}
			$(".gb-mystart.right").append($(".sw-mystart-button.signin"));

		//DOC move district home button and schoollist dropdown
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("OUR SCHOOLS");
		$(".gb-mystart.left").prepend($(".sw-mystart-dropdown.schoollist")).prepend($(".sw-mystart-button.home"));

        addFramesetTranslate();
    }

    function dynamicContent(){
    	// ********** MYSTART ********** //
    	// HERE JUST TO FIX A WAVE A11Y ISSUE
        $("#sw-mystart-search").prepend('Search this site');

		// ********** HEADER ********** //
		//DOC add sitename
		var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Downingtown Area School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        	}
        	//DOC add class to register app
        	if(($("#swageprompt-txt-birthmonth").length) && ($("#swageprompt-txt-birthday").length) && ($("#swageprompt-txt-birthyear").length)) {
        		$("#spn-content .ui-widget.app").addClass("register");
        	}
        	// SHOW SOCIAL ICONS IF THEY ARE TURNED ON
        	var socialIconSwitches = [
        		["facebook", "true"],
        		["twitter", "true"],
        		["drive", "false"],
        		["youtube", "false"],
        		["vimeo", "false"],
        		["instagram", "false"],
        		["pinterest", "false"]
        	]

        	for(var i = 0; i < socialIconSwitches.length; i++) {
        		if(socialIconSwitches[i][1] == "true") {
        			$("." + socialIconSwitches[i][0]).removeClass("hidden");
        		}
        	}

		// ********** FOOTER ********** //
		// SCROLL TO TOP BUTTON
		$(document).on("click keydown", "#gb-to-top", function(e){
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
                e.preventDefault();

                $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                	$(".sw-mystart-button.home > a").focus();
                });
                return false;
            }
		});

		//DOC get Schoolwires footer links
		$("a#feedback").text($("div#sw-footer-links li:eq(0) a").text()).attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
		$("a#terms").text($("div#sw-footer-links li:eq(2) a").text()).attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
		$("a#policy").text($("div#sw-footer-links li:eq(1) a").text()).attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		
	}

	function buildStyleSheet(){
		var slideshowTitleColor = "rgba(" + hexToRgb('#000065') + ", 0.60)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = '#hp-slideshow .rs-photo-gallery-title {' +
						'background: #000065;' +
						'background: ' + slideshowTitleColor + ';' +	
					'}' +
					'.ie8 #hp-slideshow .rs-photo-gallery-title {' +
						'background: #000065;' +
						'-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=60)";' +	
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function modEvents(){
		head.ready("modEvents", function() {
		    if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "no",
		            dayWeek     : "no"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function gbSearch() {
		$(".gb-social-icon.search").on("click keydown", "> span", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
                e.preventDefault();

                if($("#gb-search").hasClass("closed")) {
                    $("#gb-search").show().removeClass("closed").attr("aria-hidden", "false");
                    $(".gb-social-icon.search > span").attr("aria-label", "Close search");
                    $("#gb-search-input").focus();
                } else {
                    $("#gb-search").hide().addClass("closed").attr("aria-hidden", "true");
                    $(".gb-social-icon.search > span").attr("aria-label", "Open search");
                }
            }
		});
		$("#gb-search").click(function() {
			return false;
		});

		$("#gb-search").on("click keydown", ".gb-search-button", function(e){
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
		    	e.preventDefault();

		    	var searchForm = $(this).parent();
		    	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
			}
        });
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "Search...") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("Search...");
		    }
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = jsBreakPoint();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

