	function globalTabs() {
		// GET GLOBAL TAB NUMBER
		var tabNum = ('4' == "5;4;3;2;1;0") ? 5 : '4';
		var tabs = "";

		// IF WE HAVE TABS TO BUILD
		if(tabNum > 0) {
			// DECLARE GLOBAL TABS ARRAY WITH DATA
			var tabsData = [
				["Students", "/Page/2407"],
				["Parents", "/Page/4874"],
				["Community ", "/Page/2409"],
				["Staff", "/Page/2408"],
				["Safety & Wellness", "/Page/9015"]
			];

			// BUILD OUT THE GLOBAL TABS
			for(var i = 0; i < tabNum; i++) {
				var siteCalendarLink = "/Page/2";
				var tabHref = tabsData[i][1];
				tabHref = tabHref.replace("[SITECALENDARLINK]", siteCalendarLink);

				tabs += '' + tabsData[i][0] + '';
			}

			// ADD TABS TO THE DOM
			$("#gb-global-tabs, #gb-sticky-header-tabs").html(tabs);
		}
	}

	var parallaxScroll = function(){
		// GET THE TOP POSITION OF THE TABS CONTAINER
		var channelBarPos = $("#gb-page-top").height();

		if ($(window).scrollTop() < channelBarPos) {
			$("#gb-channel-list-outer").removeClass("sticky-header");
			$("#gb-page-bottom").css("padding-top", "0px");
		} else {
			$("#gb-channel-list-outer").addClass("sticky-header");
			$("#gb-page-bottom").css("padding-top", $("#gb-channel-list-outer").height() + "px");
		}
	}

	function parallaxScrollReset(){
		$("#gb-channel-list-outer").removeClass("sticky-header");
		$("#gb-page-bottom").css("padding-top", "0px");
	}

	function initLogoBgCanvas() {
		var canvas = document.getElementById("logo-bg");

		//IF IE
		if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
		var ctx = canvas.getContext("2d");

		drawLogoBgCanvas(ctx);
	}

	function drawLogoBgCanvas(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(52.7, 0.0);
		ctx.lineTo(26.8, 26.0);
		ctx.bezierCurveTo(10.3, 41.1, 0.0, 62.9, 0.0, 87.0);
		ctx.bezierCurveTo(0.0, 132.8, 37.2, 170.0, 83.0, 170.0);
		ctx.bezierCurveTo(104.9, 170.0, 124.7, 161.5, 139.6, 147.7);
		ctx.lineTo(139.7, 147.9);
		ctx.lineTo(286.8, 0.0);
		ctx.lineTo(52.7, 0.0);
		ctx.closePath();
		ctx.fillStyle = "#000065";
		ctx.fill();
		ctx.restore();
	}

	function initSlideshowCurveOneCanvas() {
