		}
	}

	function drawSlideshowCurveOneCanvas(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 275.0);
		ctx.bezierCurveTo(0.0, 176.6, 22.4, 83.3, 62.3, 0.1);
		ctx.bezierCurveTo(62.7, 0.3, 63.5, 0.2, 64.3, 0.0);
		ctx.bezierCurveTo(64.3, 192.3, 64.0, 303.3, 64.2, 506.0);
		ctx.bezierCurveTo(57.6, 505.9, 50.4, 505.9, 42.9, 506.0);
		ctx.bezierCurveTo(15.2, 434.7, 0.0, 356.1, 0.0, 275.0);
		ctx.closePath();
		ctx.fillStyle = "#000065";
		ctx.fill();
		ctx.restore();
	}

