	function initSlideshowCurveOThreeCanvas() {
		if($(".hp").length) {
			var canvas = document.getElementById("slideshow-curve-three");

			//IF IE
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");

			drawSlideshowCurveThreeCanvas(canvas, ctx);
		}
	}

	function drawSlideshowCurveThreeCanvas(canvas, ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 252.0);
		ctx.bezierCurveTo(0.0, 162.4, 18.5, 77.3, 51.9, 0.0);
		ctx.lineTo(71.9, 0.0);
		ctx.bezierCurveTo(71.9, 51.3, 72.0, 505.0, 72.0, 505.0);
		ctx.lineTo(52.2, 505.0);
		ctx.bezierCurveTo(18.5, 427.4, 0.0, 342.0, 0.0, 252.0);
		ctx.closePath();
		var grd = ctx.createLinearGradient(0, 0, canvas.width, canvas.height);
		var accentRgba = "rgba(" + hexToRgb("#FDCB25") + ", 0)";
		grd.addColorStop(0, "#FDCB25");
  		grd.addColorStop(1, accentRgba);
		ctx.fillStyle = grd;
		ctx.fill();
		ctx.restore();
	}

