	function drawChannelBarCornerCanvas(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(38.5, 0.0);
		ctx.lineTo(0.0, 30.0);
		ctx.lineTo(12.0, 0.0);
		ctx.lineTo(38.5, 0.0);
		ctx.closePath();
		ctx.fillStyle = "#000065";
		ctx.fill();
		ctx.restore();
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function jsBreakPoint(){
		// 960
		if($(".w-1024").length || $(".gt-1024").length) { return 960; } 

		// 768
		if($(".w-768").length && $(".lt-1024").length || $(".gt-768").length && $(".lt-1024").length) { return 768; }

		// 640
		if($(".w-640").length && $(".lt-768").length || $(".gt-640").length && $(".lt-1024").length) { return 640; }

		// 480
		if($(".w-480").length && $(".lt-640").length || $(".gt-480").length && $(".lt-1024").length) { return 480; }

		// 320
		if($(".lt-480").length) { return 320; }
	}

    function SLM() {
    	$.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 11943,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var schools = '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
            }
        });
    }

