		$(function(){
			var $mq = $('.marquee');
			$('.marquee').marquee({
				duration: 50000,
				gap: 20,
				delayBeforeStart: 500,
				direction: "up",
				pauseOnHover: true
			});

			//pause and resume links
			$('.pause').click(function(e){
				e.preventDefault();
				$mq.trigger('pause');
			});
			$('.resume').click(function(e){
				e.preventDefault();
				$mq.trigger('resume');
			});
			//toggle
			$('.toggle').hover(function(e){
				$mq.trigger('pause');
			},function(){
				$mq.trigger('resume');
			})
			.click(function(e){
				e.preventDefault();
			})
			$('.js-marquee-wrapper').css("margin-top","0");
		});

