liveagent.setChatWindowHeight(chatWindowHeight);
liveagent.setChatWindowWidth(chatWindowWidth);

$('.chat-online .chat-link').click(function (event) {
  event.preventDefault();
  liveagent.startChat(chatButtonId);
});

if (!window._laq) {
  window._laq = [];
}

window._laq.push(function(){
  $(".chat-offline").each(function() {
    var domElement = $(this)[0];
    liveagent.showWhenOffline(chatButtonId, domElement);
  });

  $(".chat-online").each(function() {
    var domElement = $(this)[0];
    liveagent.showWhenOnline(chatButtonId, domElement);
  });
});

