link.script:
      $(document).ready(function() {
      $('#slider_132').data('wpslider', new $.fn.wpslider( { g_nDivId:'#slider_132', g_nTransitStyle:2, g_nLoopTime:2000, g_nAnimationTime:500, g_strEasing: "swing" } ) );
      var btnState = [];
      $('#map1_1').click(function(){ if ( $(this).attr('disabled') === undefined ) $('#slider_132').data('wpslider').navigate(0);});
      $("a.ActiveButton").bind({ mousedown:function(){if ( $(this).attr('disabled') === undefined ) $(this).addClass('Activated');}, mouseleave:function(){ if ( $(this).attr('disabled') === undefined ) $(this).removeClass('Activated');}, mouseup:function(){ if ( $(this).attr('disabled') === undefined ) $(this).removeClass('Activated');}});
      $('#site_search_2_input').keypress(function(event) { if ( event.keyCode == 13 ) window.location.href = 'nesdsearchresults.html?site_search_results_2='+document.getElementById('site_search_2_input').value+'&depth=0&swr=false'; });

      $('#site_search_2_button').click(function(event) { window.location.href = 'nesdsearchresults.html?site_search_results_2='+document.getElementById('site_search_2_input').value+'&depth=0&swr=false'; });

      $('#slider_132').data('wpslider').restrictSlides();
      $("#marq_1_0").marquee();
      });
