if ($(window).scrollTop() > 0){
    $("nav").addClass("scrolled");
    $("header").addClass("scrolled");
}else{
    $("nav").removeClass("scrolled");
    $("header").removeClass("scrolled");
}
$(window).scroll(function(){
    if ($(window).scrollTop() > 0){
        $("nav").addClass("scrolled");
    $("header").addClass("scrolled");
    }else{
        $("nav").removeClass("scrolled");
    $("header").removeClass("scrolled");
    }
});

