$(document).ready(function () {
    jQuery.each($(".hpText"), function (index, value) {
        $(value).css("max-height", "");
        $(value).css("overflow-y", "");
        if ($(value).outerHeight() > 170) {
            $(value).css("max-height", "170px");
            $(value).css("overflow-y", "scroll");
        }
    });
});
$(window).resize(function () {
