meta.script:
var xOffsetB = -60;
var yOffsetB = -120;

function showIndexPopup (targetObjectId, eventObj) {
	
    if(eventObj) {
	// hide any currently-visible popups
	hideCurrentPopup();
	// stop event from bubbling up any farther
	eventObj.cancelBubble = true;
	// move popup div to current cursor position 
	// (add scrollTop to account for scrolling for IE)
	var newXCoordinate = (eventObj.pageX)?eventObj.pageX + xOffsetB:eventObj.x + xOffsetB + ((document.body.scrollLeft)?document.body.scrollLeft:0);
	var newYCoordinate = (eventObj.pageY)?eventObj.pageY + yOffsetB:eventObj.y + yOffsetB + ((document.body.scrollTop)?document.body.scrollTop:0);
	moveObject(targetObjectId, newXCoordinate, newYCoordinate);
	// and make it visible
	if( changeObjectVisibility(targetObjectId, 'visible') ) {
	    window.currentlyVisiblePopup = targetObjectId;
	    return true;
	} else {
	    return false;
	}
    } else {
	return false;
    }
}

function chooseSchool() {
	objChooser = document.getElementById("schoolList").style;
	objChooser.display == "block" ? objChooser.display = "none" : objChooser.display = "block";
}


function chooseLogin() {
	objChooser = document.getElementById("LoginChoose").style;
	objChooser.display == "block" ? objChooser.display = "none" : objChooser.display = "block";
}

td.script:
function jsOpenPopup(jsUrl,jsWidth,jsHeight,jsResizable,jsScrollable,jsLocation,jsMenuBar,jsToolBar,jsStatus)
{
if (jsResizable == null)
{
	jsResizable = 0; //jsResizable is resizeable
	//jsScrollable = 0; //jsScrollable is the scroll bar
	jsMenuBar = 0;  //jsMenuBar is the menu bar
	jsStatus = 0;  //jsStatus is the status bar
	jsLocation = 0;	 //jsLocation is the location bar
	jsToolBar = 0;	 //jsToolBar is the tool bar
}
if (jsScrollable == null)
{
	jsScrollable = 0;
}
//Look to see if this is Internet Explorer
if (navigator.appName == "Microsoft Internet Explorer")
{
    var xMax = screen.width, yMax = screen.height
	//Look to see if this is running on a Macintosh, and adjust the popup window size accordingly
	if (navigator.appVersion.indexOf("Macintosh") != -1)
	{
		jsHeight = jsHeight - 28
		jsWidth = jsWidth - 10
	}
}
else
{
	if (navigator.appName == "Netscape")
	{
		var xMax = window.outerWidth;
		var yMax = window.outerHeight;    	
		//Look to see if this is running on a Macintosh, and adjust the popup window size accordingly
		if (navigator.appVersion.indexOf("Macintosh") != -1)
		{
            if (parseInt(navigator.appVersion) >= 5)
            {
				//If this is Netscape 6	
				jsHeight = jsHeight - 25
				jsWidth = jsWidth - 7
			}
			else
			{
				//Older versions of Netscape	
				jsHeight = jsHeight - 30
				jsWidth = jsWidth - 15
			}
		}
		else
		{
			jsHeight = jsHeight - 30
			jsWidth = jsWidth - 15
		}
    }
    else
    {
        var xMax = 640
        var yMax=480
    }
}
var xOffset = (xMax - jsWidth)/2;
var yOffset = (yMax - jsHeight)/2;
var updatewin = window.open(jsUrl,"update",'top=' + yOffset + ',left=' + xOffset + ',width=' + jsWidth + ',height=' + jsHeight + ',resizable=' + jsResizable + ',scrollbars=' + jsScrollable + ',menubar=' + jsMenuBar + ',status=' + jsStatus + ',locationbar=' + jsLocation + ',toolbar=' + jsToolBar + ' ');
updatewin.window.focus();
updatewin.resizeTo(jsWidth,jsHeight);
}

