	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			tabbedApps();
			setDropdownCols();
			scroller();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView768();
			tabbedApps();
			setDropdownCols();
			scroller();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView768();
			tabbedApps();
			setDropdownCols();
			scroller();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			calendarListView();
			appView();
			districtCheck();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			calendarListView();
			appView();
			districtCheck();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 364,
				"imageControls"		: "off",
				"animateType"		: "fade", // CHOICES - "slide"
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['overlay'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "on",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "READ MORE >>" 
			});
		}
	}

	function appView768(){
		//DOC make homepage apps collapsible for small mobile devices
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active active-app");
			$(".tab-button").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".selector").text("Select a school");
		$(".sw-dropdown-selected").text("Select a school");
		$("#gb-mystart-left").append($(".sw-mystart-dropdown.schoollist"));
		$("#sw-mystart-search").prependTo("#gb-mystart-right").show();
		$(".sw-mystart-dropdown.schoollist").addClass("hide480");
		$("#sw-mystart-search").addClass("hide640");



		// ********** HEADER ********** //
		var siteName = "Wallingford-Swarthmore School District";
		siteName = siteName.split(" ");
		var siteNameLength = siteName.length;
		if(siteNameLength > 2){
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));
		} else {
			siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
		}
		siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
		$("#gb-sitename").prepend("
 " + siteNameEnd + " ");
		$("#gb-sitename").prepend("
" + siteNameBegin + "");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

