		}
	}
	function scroller() {
		if (!$("#gb-channel-list-outer").hasClass("initiated")){
			$("#gb-channel-list-outer").addClass("initiated");
			// SET WIDTHS AND HEIGHTS
			var channelBarWidth = 0;
			var channelBarHeight = $("li.sw-channel-item").height();
			$("ul.sw-channel-list li.sw-channel-item").each(function () {
				channelBarWidth += $(this).width();
			});
			var borderWidth = $("li.sw-channel-item").length * 2;
			channelBarWidth += borderWidth;
			$("ul.sw-channel-list").css("width", channelBarWidth + 10);

			// GET CURRENT WIDTH OF CHANNEL BAR CONTAINER
			var scrollWidth = 0;
			scrollWidth = channelBarWidth - $("#sw-channel-list-container").width() + 88;

			if($("#sw-channel-list-container").width() < $("#channel-navigation").width()){
				$("#gb-channel-list-outer").addClass("channel-slider");
			}
			// RESET THE WIDTH OF CHANNEL BAR CONTAINER WHEN WINDOW RESIZES
			$(window).resize(function () {
				var isiPad = navigator.userAgent.match(/iPad/i) != null;
				if (isiPad){
					if ($("#sw-channel-list-container").width() < $("#channel-navigation").width() && !$("#gb-channel-list-outer").hasClass("channel-slider")){
						scrollWidth = channelBarWidth - $("#sw-channel-list-container").width() + 88;
					} else {
						scrollWidth = channelBarWidth - $("#sw-channel-list-container").width();
					}
				} else {
					scrollWidth = channelBarWidth - $("#sw-channel-list-container").width();
				}	
				var leftPos = $("ul.sw-channel-list").css("margin-left").replace("px", "");
				if (leftPos < 0 && -scrollWidth > leftPos) {
					$("ul.sw-channel-list").animate({
						marginLeft: "-" + scrollWidth + "px"
					}, 1000);
				}
				var channelContainerWidth = 0;
				var channelListWidth = $("#channel-navigation").width();
				if ($("#gb-channel-list-outer").hasClass("channel-slider")) {
					channelContainerWidth = $("#sw-channel-list-container").width() + 88;
				} else {
					channelContainerWidth = $("#sw-channel-list-container").width();
				}
				if (channelContainerWidth < channelListWidth){
					if (!$("#gb-channel-list-outer").hasClass("channel-slider")){
						$("#gb-channel-list-outer").addClass("channel-slider");
					}
				} else {
					$("#gb-channel-list-outer").removeClass("channel-slider");
					$("#channel-navigation").css("margin-left", "0px");
				}
				setDropdownCols();
			});
			// RIGHT SCROLL FUNCTION
			$(".scroll-btn.right").live("mousedown touchstart", function() {
				$("ul.sw-channel-list").animate({
					marginLeft: "-" + scrollWidth + "px"
				}, 1500, "linear");
			});
			$(".scroll-btn.right").live("mouseup touchend", function() {
				$("ul.sw-channel-list").stop();
				setDropdownCols();
			});
			// LEFT SCROLL FUNCTION
			$(".scroll-btn.left").live("mousedown touchstart", function () {
				$("ul.sw-channel-list").animate({
					marginLeft: "0px"
				}, 1500, "linear");
			});
			$(".scroll-btn.left").live("mouseup touchend", function () {
				$("ul.sw-channel-list").stop();
				setDropdownCols();
			});
			$("#gb-channel-list").removeClass("invisible");
			setDropdownCols();
		}
	}

	function setDropdownCols(){
		$("li.sw-channel-item").each(function(scrollWidth){
			// IF WERE ON THE HOMEPAGE
			if($(".hp").length){
				// GET THE HEIGHT OF THE SLIDESHOW SO CHANNEL DROPDOWN WONT EXCEED IT
				// ADD 15PX FOR TOP MARGIN OF SLIDESHOW
				var slideshowHeight = $("#hp-slideshow-outer").height() + 15;

				// SET THE HEIGHT
				$(".sw-channel-dropdown").css("height", slideshowHeight + "px");

				if($(this).find(".sw-channel-dropdown").length){
					// GET THE HEIGHT OF A SINGLE SECTION IN DROPDOWN
					var channelHeight = (isMSIE) ? 32 : 32;

					// FIGURE OUT HOW MANY SECTIONS WE CAN FIT WITHIN THE DROPDOWN THAT HAS A FIXED HEIGHT
					var colOneNum = Math.floor(slideshowHeight / channelHeight);

					// RESET THE DROPDOWNS IF NEEDED
					if($(this).find(".sw-channel-dropdown .section-col").length) {
						$(this).find(".sw-channel-dropdown").append($(this).find(".sw-channel-dropdown .section-col ul").children());
						$(this).find(".sw-channel-dropdown .section-col").remove();
					}
					$(this).find(".sw-channel-dropdown li").css("padding-left", "0px");

					var channelLis = $(this).find(".sw-channel-dropdown li");
					var channelLiNum = channelLis.length;

					// ONLY IF WE NEED TO MAKE COLUMNS
					if(channelLiNum > colOneNum){
						// PUT THE FIRST GROUP TO SECTIONS IN COLUMN ONE
						channelLis.slice(0, colOneNum).wrapAll("
");

						// PUT REMAINING SECTIONS IN COLUMN TWO
						channelLis.slice(colOneNum, channelLiNum).wrapAll("
");
					}

					// ADJUST THE LEFT POSITION OF THE SECTIONS TO LINE UP WITH THEIR CORRESPONDING CHANNEL
					if($("#gb-channel-list-outer").hasClass("channel-slider")){
						if($(this).find(".sw-channel-dropdown .section-col").length){
							$(this).find(".sw-channel-dropdown .section-col.one").show().css("padding-left", ($(this).position().left + 44) + "px")
						} else {
							$(this).find(".sw-channel-dropdown li").show().css("padding-left", ($(this).position().left + 44) + "px");
						}
					} else {
						if($(this).find(".sw-channel-dropdown .section-col").length){
							$(this).find(".sw-channel-dropdown .section-col.one").show().css("padding-left", ($(this).position().left) + "px");
						} else {
							$(this).find(".sw-channel-dropdown li").show().css("padding-left", ($(this).position().left) + "px");
						}
					}

					// SET A MAX-WIDTH ON THE COLUMNS
					var colWidthDiff = ($("#sw-channel-list-container").width() - $(this).position().left) / 2;
					var colWidth = Math.floor((colWidthDiff / $("#sw-channel-list-container").width()) * 100);
					$(this).find(".sw-channel-dropdown .section-col").css("max-width", colWidth + "%");
				}
			} else {
				if($(this).find(".sw-channel-dropdown").length){
					if($("#gb-channel-list-outer").hasClass("channel-slider")){
						$(this).find(".sw-channel-dropdown li").show().css("padding-left", ($(this).position().left + 44) + "px");
					} else {
						$(this).find(".sw-channel-dropdown li").show().css("padding-left", ($(this).position().left) + "px");
					}
				}
			}
		});
	}

