    });

    function addFramesetTranslate() {
        var pageURL = window.location.href;
        pageURL = encodeURIComponent(pageURL);
        var pageHost = window.location.host;
        if (pageHost != "translate.googleusercontent.com") {
            if (translateSwitch == "Branded") {
                $('

Translate

Translate
').prependTo('#google_translate_element');
                $(".sw-mystart-dropdown.translate .sw-dropdown .sw-dropdown-list").languageTranslate({
                    "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                    "advancedOptions": {
                        "addMethod": "prepend", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                        "dropdownHandleText": "Translate", // ONLY FOR FRAMESET AND API VERSIONS
                        "useTranslatedNames": true // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
                    }
                });
            } else {
                $("#google_translate_element").languageTranslate({
                    "type": 1, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                    "advancedOptions": {
                        "addMethod": "prepend", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                        "dropdownHandleText": "Translate", // ONLY FOR FRAMESET AND API VERSIONS
                        "useTranslatedNames": true // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
                    }
                });
            }
        }

    }
    $(window).load(function () {
        MyStartDropdownActions({
            "dropdownParent": ".sw-mystart-dropdown",
            "dropdownSelector": ".selector",
            "dropdown": ".sw-dropdown",
            "dropdownList": ".sw-dropdown-list"
        });

        $(".sw-mystart-dropdown.translate.branded").unbind();
        $(".sw-mystart-dropdown.translate.branded").mouseleave(function (e) {
            if (e.target.nodeName.toLowerCase() !== 'select') {
                $(".sw-dropdown-arrow").removeClass("rotate-arrow");
                $(".sw-dropdown", this).slideUp("slow", "swing");
                $(this).removeClass("open");
            }
        });

    });

    function MyStartDropdownActions(params) {
        var dropdownParent = params.dropdownParent;
        var dropdownSelector = params.dropdownSelector;
        var dropdown = params.dropdown;
        var dropdownList = params.dropdownList;

        // MYSTART DROPDOWN SELECTOR CLICK EVENT
        $(document).on("click", dropdownParent + " " + dropdownSelector, function (e) {
            e.preventDefault();
            if ($(this).parent().hasClass("open")) {
                $("+ " + dropdownList + " a").attr("tabindex", "-1");
                $(this).attr({ "aria-expanded": "false" }).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
            } else {
                $(this).attr({ "aria-expanded": "true" }).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing");
            }
        });
        $(dropdownParent).mouseleave(function () {
            $("+ " + dropdownList + " a", this).attr("tabindex", "-1");
            $(dropdownSelector, this).attr({ "aria-expanded": "false" }).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
        }).focusout(function () {
            var thisDropdown = this;
            setTimeout(function () {
                if (!$(thisDropdown).find(":focus").length) {
                    $(dropdownSelector, thisDropdown).attr({ "aria-expanded": "false" }).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }, 500);
        });
    }

main.script:
            function playvideo(id) {
                var cls = "#" + id;
                var playbtn = "#s_" + id;
                var video = $(cls).get(0);
                if (video.paused) {
                    video.play();
                    //$(cls).prop('controls', 'controls');
                    $(playbtn).addClass("hide");
                }
                else {
                    video.pause();
                    $(cls).removeAttr('controls');
                    $(playbtn).removeClass("hide");
                }
            }

            function callVideo(videoType, VideoURL, Vid) {
                if (videoType == 1) {
                    $('#vid-' + Vid).html('');
                    var html = VideoURL;
                    $('#vid-' + Vid).append(html);
                    //var id = '#vid-' + Vid+ ' iframe';
                    //var vi = $(id);
                    //vi.attr("src", vi.data("autoplay-src"));
                }
                else {
                    var typr = "";
                    if (VideoURL.indexOf(".mp4") >= 0) {
                        typr = "audio/mp4";

                    } else if (VideoURL.indexOf(".ogg") >= 0) {
                        typr = "audio/ogg";

                    } else if (VideoURL.indexOf(".webm") >= 0)

                    { typr = "audio/webm"; }


                    var html = "";
                    html = html + " Your browser does not support the video.";
                    html = html + "
";
                    html = html + "";
                    $('#vid-' + Vid).html('');
                    $('#vid-' + Vid).append(html);


                }
                playvideo(Vid);

            }

            (function () {

                var youtube = document.querySelectorAll(".youtube-video-box");
                for (var i = 0; i < youtube.length; i++) {
                    var source = "https://img.youtube.com/vi/" + youtube[i].dataset.embed + "/0.jpg";
                    var image = new Image();
                    image.src = source;
                    image.className = "yt_img";
                    image.addEventListener("load", function () {
                        youtube[i].appendChild(image);
                    }(i));
                    youtube[i].addEventListener("click", function () {
                        var iframe = document.createElement("iframe");
                        iframe.setAttribute("frameborder", "0");
                        iframe.setAttribute("allowfullscreen", "");
                        iframe.setAttribute("src", "https://www.youtube.com/embed/" + this.dataset.embed + "?rel=0&showinfo=0&autoplay=1");
                        this.innerHTML = "";
                        this.appendChild(iframe);
                    });
                }
                ;

            })();

