	$(document).ready(function(){
		templatePath = "/cms/lib/TX21000357/Centricity/Template/4/";
		schoolLevel();
	});
	function initialize() {
		dynamicStylesheet();
		customLogo();
		useChannelForSection();
		dynamicContent();
		globalIcons();
		loadFlash();
		sameHeight($(".column"));
		//PHOTO GALLERY CONFIGURATION
		$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").galleryRotate({
			"width"	 : 719,
			"height" : 295
		});
		templateLibrary();
	}
	function schoolLevel() {
		if(schoolTextFiles == "on") {
			$.ajax({
			  url: "/cms/lib/TX21000357/Centricity/Template/4/setup/lipan.txt",
			  dataType: "script",
			  success: function() {
			    initialize();
			  },
			  error: function()
			  {
			  	alert("An error has occured initializing setup files.");
			  }
			});
		}
		else
		{
			initialize();
		}

	}
	function dynamicStylesheet() {
		var dynStyleSheet = document.createElement('style');
		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];
			if(head) {
	      			head.appendChild(dynStyleSheet);
			}
			var dynStyles = "a, a:visited {" +
					"	color:" + linkColor + ";" +
					"	text-decoration:" + linkDecoration + ";" +
					"}" +
					"ul.sw-channel-list li.sw-channel-item > a {" +
					"	color: " + channelTextColor + ";" +
					"}" +
					"ul.sw-channel-list li.sw-channel-item.active > a, ul.sw-channel-list li.sw-channel-item.hover > a, ul.sw-channel-list li.sw-channel-item > a:hover {" +
					"	background-color: " + channelBarColorActive + ";" +
					"	color: " + channelTextColorActive + ";" +
					"}" +
					"ul.sw-channel-dropdown li a {" +
					"	color: " + channelDropdownTextColor + ";" +
					"}" +
					"ul.sw-channel-dropdown li a:hover {" +
					"	background: " + channelDropdownColorHover + ";" +
					"	color: " + channelDropdownTextColorHover + ";" +
					"}" +
					".sw-mystart-button, #sw-mystart-account, #sw-mystart-search, .sw-mystart-dropdown .sw-dropdown-list li {" +
					"	background-color: " + mystartButtonColor + ";" +
					"}" +
					".sw-mystart-button:hover, #sw-mystart-account:hover, .sw-mystart-dropdown .sw-dropdown-list li:hover {" +
					"	background-color: " + mystartButtonColorHover + ";" +
					"}" +
					".sw-mystart-button:hover a, #sw-mystart-account:hover {" +
					"	color: " + mystartTextColorHover + ";" +
					"}" +
					".sw-mystart-button a, .sw-mystart-button a:visited, #sw-search-input, #sw-mystart-account, .sw-mystart-dropdown .sw-dropdown-list li a {" +
					"	color: " + mystartTextColor + ";" +
					"}" +
					".sw-mystart-button a:hover, .sw-mystart-dropdown .sw-dropdown-list li:hover a, .sw-mystart-dropdown .sw-dropdown-list li a:hover  {" +
					"	color: " + mystartTextColorHover + ";" +
					"}" +
					"#gb-footer a:visited {" +
					"	color: " + footerTextColor + ";" +
					"}";
			var rules = document.createTextNode(dynStyles);
			if(dynStyleSheet.styleSheet){// IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}
	function useChannelForSection() {
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
	}
	function customLogo() {
		if(customLogos != "none") {
			if(customLogos == "on") {
				$("#gb-logo a img").attr("src","/cms/lib/TX21000357/Centricity/Template/4/logos/lipan.png");
			} else {
				$("#gb-logo a img").attr("src","/cms/lib/TX21000357/Centricity/Template/4/logos/_logo.png");
			}
			$("#gb-logo").show(); 
		}
	}
	function dynamicContent() {
		//TOGGLES BREADCRUMBS ON/OFF
		if(breadcrumbToggle == "on"){
			$("#sp-breadcrumbs").show();
		}

		//Modify Mystart
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
	        $("#sw-search-button").text("search");

		//SETS DISTRICT BUTTON ON/OFF
		if(districtButton == "off"){
			$(".sw-mystart-button.home").hide();
		}

		//SETS BACKGROUND COLORS
		$("body").css("background-color",backgroundColor);

		//SETS PRIMARY COLORS
		$("#gb-header").css("background-color",primaryColor);
		$("div.ui-hp#sw-content-container1 div.ui-widget-detail, div.ui-hp#sw-content-container1 div.ui-widget-footer").css("background-color",primaryColor);
		$("#gb-footer").css("background-color",primaryColor);
		$(".sp.column.one div.ui-widget-detail, div.sp.column.one div.ui-widget-footer").css("background-color",primaryColor);

		//SETS SECONDARY COLORS
		$("div.ui-hp#sw-content-container1 div.ui-widget-header").css("background-color",secondaryColor);
		$(".sp.column.one div.ui-widget-header").css("background-color",secondaryColor);

		//SETS ACCENT COLORS

		//SETS CHANNEL BAR STYLES
		$("#sw-channel-list-container").css("background-color", channelBarColor);
		$("li.sw-channel-item.active > a").css("color", channelTextColorActive);
		$("ul.sw-channel-dropdown").css("background-color", channelDropdownColor);
		$("ul.sw-channel-dropdown").css("border-color", backgroundColor);
		//**DROPDOWN TEXT COLOR AND TEXT HOVER STATE IS SET IN THE DYNAMIC STYLESHEET FUNCTION**

		//SETS MYSTART STYLES
		$("#sw-mystart-outer").css("background-color", mystartBarColor);
		$(".sw-mystart-dropdown, .sw-dropdown, .sw-dropdown-selected").css({"background-color":mystartButtonColor,"color":mystartTextColor});
		$("#sw-search-button").css({"background-color":mystartButtonColorHover,"color":mystartTextColorHover});

		//SETS SCHOOLNAME
		if(schoolNameText == "default")
		{
			schoolNameText = "Lipan ISD";
		}
		$("h1#gb-name a").html(schoolNameText);
		//SETS SCHOOLNAME COLOR
		$("h1#gb-name a").css("color",schoolNameColor);

		//SETS TAGLINE
		$("h2#gb-tagline").html(taglineText);
		//SETS TAGLINE COLOR
