	function globalIcons() {
		var iconSize = 50;
		for(i=0; i<numberIcons; i++){ 
			var globalLink = window["icon" + (i+1) + "Link"];
			var globalTarget = window["icon" + (i+1) + "Target"];
			var globalImage = window["icon" + (i+1) + "Image"];
			var globalText = window["icon" + (i+1) + "Text"];
			var flashIcon = "#icon-movie-number"+ (i+1);
			var currentFlash = "flash-icon-number" + (i+1);
			var currentFlashHolder = "flash-icon-holder-number" + (i+1);
			var sheetSize = iconSize * 20; 
			//GET POSITION OF THE ICON
			var myIconType = globalImage.split(":");
			//CHECK FOR SITEALIAS STRING
			globalLink = globalLink.replace("[SITEALIAS]","lipan");
			//CHECK FOR GLOBAL CALENDAR LINK
			globalLink = globalLink.replace("[SITECALENDARLINK]","/Page/2");
			//CHECK FOR GLOBAL SITEMAP LINK
			globalLink = globalLink.replace("[SITEMAPLINK]","/site/Default.aspx?PageType=15&SiteID=1&SectionMax=15&DirectoryType=6");
			var positionVal = Math.round(((80-iconSize)/2));
			$("#gb-global-icons").append("" + globalText + "");
			$("a.global-icon span.text").css("color",iconColor);
			if(myIconType[0] == "system"){
				var myPositions = myIconType[1].split("_");
		        	var myTop = (myPositions[0].replace("r","") - 1) * (iconSize*-1);
		        	var myLeft = (myPositions[1].replace("c","") - 1) * (iconSize*-1);
		        	//ANDROID CONDITION
			        var agent = navigator.userAgent.toLowerCase();
			        if(agent.indexOf("android") != -1 || agent.indexOf("series60") != -1 || agent.indexOf("symbian") != -1 || agent.indexOf("windows ce") != -1 || agent.indexOf("blackberry") != -1) {
			        	//DO NOTHING
			        } else {
					//LOAD FLASH ICON SHEET
					var so = new SWFObject("/cms/lib/TX21000357/Centricity/Template/4/icons/icons.swf", "icon-movie-number" + (i+1), sheetSize, sheetSize, "9", "#FFFFFF");
					so.addParam("wmode", "transparent");
				        so.addVariable("iconColor", iconColor);
				        so.write(currentFlashHolder);
			        }
			        //NON FLASH
			        if(!$(flashIcon).size()){
			        	$("#" + currentFlashHolder).append("").css({"top":myTopPX,"left":myLeftPX});
			        }
			        var myTopPX = myTop+"px";
				var myLeftPX = myLeft+"px";
				$("#" + currentFlashHolder).css({"top":myTopPX,"left":myLeftPX});
		        }
		        if(myIconType[0] == "custom"){
		        	$("#" + currentFlashHolder).append("");
		        }
		}
		//FOR SELF-SERVICE
		$("body").append("
");
		$("a.global-icon:last").clone().appendTo("#global-icon-html");
		var lastIconNum = "number" + numberIcons;
    		var regex = new RegExp(lastIconNum, "g");
		var globalIconHTML = $("#global-icon-html").html();
		globalIconHTML = globalIconHTML.replace(regex,"number99");
		$("#global-icon-html").empty().append(globalIconHTML);	
		$("#icon-number99 .text").html("new");	
		$("#flash-icon-holder-number99").css({"top":"0px","left":"0px"});
	}
	function loadFlash() {
		if($("#hp-slideshow-overlay").size())
	        {
	        var so = new SWFObject("/cms/lib/TX21000357/Centricity/Template/4/scripts/slideshow-overlay.swf","mymovie", "719", "320", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("primaryColor", primaryColor);
	            so.addVariable("secondaryColor",accentColor);
	            so.write("hp-slideshow-overlay");
	        }
	        else if($("#sp-flash").size())
	        {
	       	    var so = new SWFObject("/cms/lib/TX21000357/Centricity/Template/4/scripts/sp-flash.swf","mymovie", "719", "120", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("primaryColor", primaryColor);
	            so.addVariable("secondaryColor",accentColor);
	            so.write("sp-flash");
	        }     
	}
	function sameHeight(selector) {
		if($("#sp-content").size()){
			selector.height(function(){
				$(this).height($(this).parent().height());
				//var spHeight = $(".sp.column.one").height() - $(".sp.column.one .ui-widget.app").height();
				//$(".sp.column.one .ui-widget.app .ui-widget-detail").height($(".sp.column.one .ui-widget.app").height() + spHeight);
			});

			selector.children().resize(function(){
				selector.height("auto");
				selector.height(function(){
					$(this).height($(this).parent().height());

				});
			});
		}	

	}
