$('#galleria1675').show();
Galleria.run('#galleria1675', {
height:0.75,
width:600,
_moduleInstanceID:
1675,
_allowAudio: true,
_hasAudio: false,
_audioLoop: false,
_audioSrc: '',
popupLinks:true,

dummy: staticURL + '/GlobalAssets/Scripts/ThirdParty/galleria/themes/classic/gallery_error.png',
transition:'fade',
autoplay:3000
});
Galleria.ready(function() {
 this.bind('image', function(e) {
     e.imageTarget.alt = e.galleriaData.description;
 });
 this.bind('thumbnail', function(e) {
     e.thumbTarget.alt = e.galleriaData.description;
 });
});
});
function checkAudioTagSupport() {
var bResult = false;
var myAudio = document.createElement('audio');
if (myAudio.canPlayType) {
var canPlayMp3 = !!myAudio.canPlayType && '' != myAudio.canPlayType('audio/mpeg');
if (canPlayMp3) {
bResult = true;
}
}
return bResult
}












