$(".group865854").colorbox({rel:'group865854', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group865854").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery865854 = $("a[rel=group865854]").colorbox({maxWidth: "80%"});
$("a#openGallery865854").click(function(e){
    e.preventDefault();
    $gallery865854.eq(0).click();
});

