	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				tabbedApps();
				$(window).scroll(parallaxScroll);
			break;
			case "768":
				appView();
			break;
			case "640":
				appView();
			break;
			case "480":
				appView();
				globalTabWidth();
			break;
			case "320":
				appView();
				globalTabWidth();
			break;
		}
	}

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		//if(!$("div.ui-widget.app.active").length){
			//	$("#hp-content div.ui-widget.app:first").addClass("active");
			//}

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("div.ui-hp div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
			$('#hp-content .ui-widget.app').each(function(){
					if(!$(".ui-widget-app-detail", this).length) {
					    	$(".ui-widget-detail",this).next('.ui-widget-footer').andSelf().wrapAll('
');
					}   	
			});

		if(!appViewEvents) {	
			$(".ui-widget-header").click(function(){
		                if($(this).parent().hasClass("active")) {
		                	$(this).siblings(".ui-widget-app-detail").slideUp(function(){
		                		$(this).removeClass("active").removeAttr("style");
		                	});
					$(this).parent().removeClass("active");			
				} else {
					$("div.ui-widget.app .ui-widget-app-detail.active").slideUp(function(){
						$(this).removeClass("active").removeAttr("style");

					});
					$("div.ui-widget.app.active").removeClass("active");
					$(this).parent().addClass("active");
					$(this).siblings(".ui-widget-app-detail").slideDown(function(){
						$(this).addClass("active").removeAttr("style");
					});	
				}
			});
			appViewEvents = true;
		}
	}	

	function tabbedApps(){	
		head.ready("tabBlocks", function() {
			//TABBED APPS
	        	if(!$(".tabs-container").length){
		        	$("div.hp.column.one div#hp-tabs-one div#sw-content-container1, div.hp.column.two div#hp-tabs-two div#sw-content-container2, div.hp.column.two div#hp-tabs-three div#sw-content-container5").tabBlocks();
		        	$(".tab-button:eq(0)").addClass("first");

		        	//LIMIT TO TWO APPS IN TABBED REGIONS
		        	$("div#hp-tabs-one .tab-button:gt(1)").remove();
				$("div#hp-tabs-one .ui-widget.app:gt(1)").remove();
				$("div#hp-tabs-two .tab-button:gt(1)").remove();
				$("div#hp-tabs-two .ui-widget.app:gt(1)").remove();
				$("div#hp-tabs-three .tab-button:gt(1)").remove();
				$("div#hp-tabs-three .ui-widget.app:gt(1)").remove();

				if("true" == "true") {
					$("div#hp-tabs-three .tab-button:eq(0)").addClass("facebook");
					$("div#hp-tabs-three .tab-button:eq(0) .tab-right").addClass("icon-font-facebook").text("");
					$("div#hp-tabs-three .tab-button:eq(1)").addClass("twitter");
					$("div#hp-tabs-three .tab-button:eq(1) .tab-right").addClass("icon-font-twitter").text("");
				}
			}
		});	
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

