	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function globalTabWidth() {
		//GLOBAL TAB WIDTH
		var bpWidth = $(window).width();
		if (bpWidth < 640) {
			var tabWidth = (bpWidth - 15)/4 + "px";
			$("#gb-global-tabs .global-tab").css("width", tabWidth);
		}	
		$(window).resize(function(){
			var bpWidth = $(window).width();
			if (bpWidth < 640) {
				var tabWidth = (bpWidth - 15)/4 + "px";
				$("#gb-global-tabs .global-tab").css("width", tabWidth);
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");

				$(".ui-widget.app.upcomingevents").each(function() {
					$(this).removeAttr("style");
				});
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

    function accessibility() {

    	$("#sw-search-button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.space:
                	e.preventDefault();
                    SWGoToSearchResultsPageswsearchinput();

                break;
            }
        });

        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1)).attr("aria-expanded", "false").attr("role","button").attr("aria-haspopup","true").removeClass("aria-hidden");
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        $("#gb-schoollist .sw-mystart-dropdown .sw-dropdown-selected").remove();

        $(".sw-mystart-dropdown.schoollist").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();

                    if(!$(this).hasClass("open")) {
                        // TOGGLE THE DROPDOWN
                        $(this).addClass("open");
                        $(".selector", this).attr("aria-expanded", "true").attr("aria-hidden","false");
                        $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden", "false");
                        $(".sw-dropdown-list", this).attr("aria-hidden","false");
                        $(".sw-dropdown-list li:first-child a", this).focus();
                    } else {
                        $(this).removeClass("open");
                        $(".selector", this).attr("aria-expanded", "false").attr("aria-hidden","true");
                        $(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden", "true");
                        $(".sw-dropdown-list", this).attr("aria-hidden","true");
                    }


                break;
            }
        });
        $(".sw-mystart-dropdown.translate").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();

                    if(!$(this).hasClass("open")) {
                        // TOGGLE THE DROPDOWN
                        $(this).attr("aria-expanded", "true").addClass("open");
                        $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden", "false");
                        $(".sw-dropdown-list li:first-child a", this).focus();
                    } else {
                        $(this).attr("aria-expanded", "false").removeClass("open");
                        $(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden", "true");
                    }


                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;

                 // CONSUME END KEY
                case csKeyCodes.tab:
                case csKeyCodes.esc:
                    e.preventDefault();

                    // CLOSE DROPDOWN
                    $(this).parent().parent().parent().parent().find(".selector").attr("aria-expanded","false").attr("aria-hidden","true");
                    $(this).parent().parent().attr("aria-hidden", "true");
                    $(this).parent().parent().parent().slideUp("slow", "swing").attr("aria-hidden", "true");
                    $(this).parent().parent().parent().parent().removeClass("open").focus();    
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        });

        $("#sw-myaccount").focus(function(e) {
                  // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                  if(!$(this).hasClass("open")) {
                      // TOGGLE THE DROPDOWN
                      $(".icon-font",this).toggleClass("open");
                        var txt = $(".icon-font.open", this).is(':visible') ? '_' : '+';
                        $(".icon-font", this).text(txt);
                      $(this).parent().toggleClass("open");
                      $("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");
                  }
              });

        $("#cs-sw-myaccount-list").focusout(function() {
          var $this = $(this);

          // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
          setTimeout(function() {
              // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
              if(!$this.is(":focus") && !$this.find(":focus").length) {
                  // TOGGLE THE DROPDOWN
                  $(".icon-font",this).toggleClass("open");
                var txt = $(".icon-font.open", this).is(':visible') ? '_' : '+';
                $(".icon-font", this).text(txt);
                  $this.toggleClass("open");
                  $("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");
              }
          }, 10);
        });

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });

    }

	function buildStyleSheet(){
