$(window).load(function(){
//--> Added by Matt K 
    $('.sw-channel-item > a').keydown(function(e){
                var c = e.which,
                href = $(this).attr('href');
        if(c == 13 || c == 32) {
                window.location.href = href;
        }
    });
    //--> End 
});  //--> End Blackboard added script



