    function customChannelBar() {
    	// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");

		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        //DOC add aria to channels and dropdown items
		$(".sw-channel-item").attr("aria-haspopup","true");
		$(".sw-channel-dropdown li").attr("aria-haspopup","false");

        /*$(".sw-channel-item .sw-channel-dropdown").each(function() {
        	$("
").appendTo(this);
        });*/

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
        	$(".sisd-custom-dropdown").remove();
        	$(".sw-channel-item").removeClass("cs-hover");
            $(".sw-channel-dropdown").slideUp(300, "swing");
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
            	$(this).addClass("cs-hover");
            	$(subList).clone().addClass("sisd-custom-dropdown max-width cs-hover").attr("aria-hidden","false").prependTo(".main-content-container").slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(this).removeClass("hover");
		});

        $(".sw-channel-item > a").focus(function(){
        	$(".sw-channel-item").removeClass("cs-hover");
            $(".sisd-custom-dropdown").remove();
            $(".sw-channel-dropdown").slideUp(300, "swing");
			var subList = $(this).parent().children('ul');
			if ($.trim(subList.html()) !== "") {
            	$(this).parent().addClass("cs-hover");
            	$(subList).clone().addClass("sisd-custom-dropdown max-width cs-hover").attr("aria-hidden","false").prependTo(".main-content-container").slideDown(300, "swing");
			}
			$(this).parent().addClass("hover");
        });

        $("li.sw-channel-item.has-dropdown > a").keydown(function(e) {
        	switch(e.keyCode) {
            	case csKeyCodes.down:
					e.preventDefault();
                    $(".sw-channel-dropdown.sisd-custom-dropdown.cs-hover li:first-child > a").focus();
				break;
                case csKeyCodes.up:
					e.preventDefault();
                    $(".sw-channel-dropdown.sisd-custom-dropdown.cs-hover li:last-child > a").focus();
				break;
            }
        });

        $(".sisd-custom-dropdown li:first-child").addClass("first");
        $(".sisd-custom-dropdown li:last-child").addClass("last");

        $(".sisd-custom-dropdown li a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();
					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS FIRST ITEM
						$(".sw-channel-dropdown li:last-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();
					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".sw-channel-dropdown li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".sw-channel-dropdown li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".sw-channel-dropdown li:last-child > a").focus();
				break;

                // CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();
					$("li.sw-channel-item.cs-hover > a").focus();
				break;

			}
		});

        //DOC change HOME channel text to icon
        $(".sw-channel-item > a span").each(function() {
        	var thisChannel = this;
            var getChannelText = $(this).text();
        	if(getChannelText == "Home") {
            	$(thisChannel).parent().parent().addClass("cs-home-icon");
                $(thisChannel).parent().attr("aria-label","Home");
            	$(thisChannel).addClass("sisd-icon-font").html("");
        	}
        });

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

        $("#gb-channel-list-outer").show();

        //MAKE CHANNEL BAR HOME LINK GO TO DISTRICT HOME INSTEAD OF SITE HOME
		$(".sw-channel-item.first a").attr("href", "/"); /*added by Lyndsay - Case 00345610 */
    }

