		}


        	if($(".sw-mystart-button.register").length) {
            	$(".sw-mystart-button.register").prependTo("#gb-mystart-inner");
            }
        	$(".sw-mystart-button.signin").prependTo("#gb-mystart-inner");


        //SEARCH MENU
        $("#sw-search-button span").text("GO");
        $("#sw-mystart-search").prependTo("#gb-search-menu");
        $(".gb-search-btn").click(function() {
        	var searchBtn = this;
            $(this).toggleClass("closed");
        	if($(searchBtn).hasClass("closed")) {
            	$(this).attr("aria-expanded","false");
        		$(searchBtn).attr("aria-label","Close Search Menu");
                $("#gb-search-menu").attr({
                	"aria-hidden": "true"
                }).slideUp();
                $('.gb-keep-safe').attr({
                	"aria-hidden" : "false",
                    "tabindex" : "0"
                });
            } else {
            	$(searchBtn).attr("aria-label","Open Search Menu");
                $(this).attr("aria-expanded","true");
                $("#gb-search-menu").attr({
                	"aria-hidden": "false"
                }).slideDown();
                $('.gb-keep-safe').attr({
                	"aria-hidden" : "true",
                    "tabindex" : "-1"
                });
            }
        });

        $(".gb-search-btn").keydown(function(e) {
        	var searchBtn = this;
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();

                if($(this).attr("aria-expanded") == "false") {
                    $(this).attr({"aria-expanded":"true", "aria-label":"Close Search Menu"});
                    $("#sw-search-input, #sw-search-button").attr("tabindex","0");
                    $("#gb-search-menu").attr("aria-hidden", "false").slideDown(function() {
                        $("#sw-search-input").focus();
                    });
                } else {
                    $(this).attr({"aria-expanded":"false", "aria-label":"Open Search Menu"});
                    $("#sw-search-input, #sw-search-button").attr("tabindex","-1");
                    $("#gb-search-menu").attr("aria-hidden", "true").slideUp();
                }
            }
        });
        $("#gb-search-menu").keydown(function(e) {
            if(e.keyCode == csKeyCodes.esc) {
                e.preventDefault();

                $(".gb-search-btn").attr({"aria-expanded":"false", "aria-label":"Open Search Menu"});
                $("#sw-search-input, #sw-search-button").attr("tabindex","-1");
                $("#gb-search-menu").attr("aria-hidden", "true").slideUp();
            }
        });


        addTranslate();

		//UNBIND SYSTEM CLICK EVENT - MYSTART DROPDOWNS
		$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
        	//$(".sw-dropdown-arrow",this).addClass("rotate-arrow");
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'true');;
			$(this).addClass("open");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
        	//$(".sw-dropdown-arrow",this).removeClass("rotate-arrow");
			$(".sw-dropdown",this).slideUp(300, "swing");
			$(this).removeClass("open");
		});
        $(document).off("focus", ".sw-mystart-dropdown");
		$(document).on("focus", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            $("#gb-mystart-inner").addClass("dropdown-active");
			$(this).addClass("open");
		});
        $(".sw-mystart-dropdown").focusout(function() {
        	$("#gb-mystart-inner").removeClass("dropdown-active");
