function MM_jumpMenuGo(objId,targ,restore){ //v9.0
  var selObj = null;  with (document) { 
  if (getElementById) selObj = getElementById(objId);
  if (selObj) eval(targ+".location='"+selObj.options[selObj.selectedIndex].value+"'");
  if (restore) selObj.selectedIndex=0; }
}

    $(function() {
        CreativeTemplate.Init(); //tip of the iceberg
    });

    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,
        "ChannelBarIsSticky": false,



        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            window.csTemplate = this;

            this.SetTemplateProps();
            this.GlobalIconsMain();
            this.GlobalIconsMystart();
            this.MyStart();
            this.Translate();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Body();
            this.RsMenu();
            this.JsMediaQueries();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.ArticleSlider();
            this.AppAccordion();
            this.SocialIcons();
            this.Footer();

            this.BuildStylesheet();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
            });
            $(window).scroll(function() { window.csTemplate.WindowScroll(); });

            var ssOverlayDuration = "";
            var ssOverlayDurationMS = ssOverlayDuration * 1000;

            setTimeout(function(){ $("#hp-slideshow").addClass("hidden-overlay") }, ssOverlayDurationMS);

            $(".mmg-description-outer").prepend($(".mmg-container .mmg-viewer .mmg-control"));
            $(".mmg-description-outer .mmg-control").wrapAll('

');
            $(".canvas-outer").appendTo(".mmg-description-outer").removeClass("hidden");

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    
            this.Search();

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-item ul").stop(true, true);
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            this.Logo();
            if($('.hp').length) {
            	initCurve();
            	initCurveTwo();
            	initCurveThree();
            }
            initFooterCurve();
            initGICurve();

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            //$(".mmg-description-outer").prepend($(".mmg-container .mmg-viewer .mmg-control"));
            //$(".mmg-description-outer .mmg-control").wrapAll('

');
            //$(".canvas-outer").appendTo(".mmg-description-outer").removeClass("hidden");

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.Logo();
            this.StickyChannelBar();
        },
        "WindowScroll": function() {
        	this.StickyChannelBar();
            var scrollTop = $(window).scrollTop();

            if(!$(".lt-1024").length){
                if(scrollTop >= 90){
                    $("#gb-header-outer").addClass("shadow");
                } else {
                    $("#gb-header-outer").removeClass("shadow");
                }
            }
        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },



        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

          // 
        "BuildStylesheet": function() {

            var accentColor82 = "rgba(" + this.HexToRgb('#c9c9c9') + ", .82)";
            var primaryColor35 = "rgba(" + this.HexToRgb('#097cb2') + ", .35)";
            var primaryColor68 = "rgba(" + this.HexToRgb('#097cb2') + ", .68)";
            var primaryColor85 = "rgba(" + this.HexToRgb('#097cb2') + ", .85)";
            var primaryColor92 = "rgba(" + this.HexToRgb('#097cb2') + ", .92)";

            var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#gb-global-icons-outer {' +
					'	background: ' + accentColor82 + ';' +
                    '}' +
                    '	.cs-global-icon:hover .icon {' +
					'		color: ' + primaryColor68 + ';' +
                    '}' +
                    '	.cs-global-icon:focus .icon {' +
					'		color: ' + primaryColor68 + ';' +
                    '}' +
                    '	#gb-social-icons .icn {' +
					'		background: ' + primaryColor85 + ';' +
                    '}' +
                    '	.gb-mystart .cs-mystart-dropdown.translate:hover .icon-font-translate:before {' +
					'		color: ' + primaryColor68 + ';' +
                    '}' +
                    '	.gb-mystart .cs-mystart-dropdown.user-options:hover .icon-font-options:before {' +
					'		color: ' + primaryColor68 + ';' +
                    '}' +
                    '	#hp-content-row-two-outer:before {' +
					'		background: ' + primaryColor85 + ';' +
                    '}' +
                    '	#hp-content-row-one a.more-link:before {' +
					'		background: ' + primaryColor35 + ';' +
                    '}' +
                    '	#hp-content-row-one a.view-calendar-link:before {' +
					'		background: ' + primaryColor35 + ';' +
                    '}' +
                    '	#hp-content-row-one .ui-article.hover .ui-article-info.has-thumb {' +
					'		background: ' + primaryColor85 + ';' +
                    '}' +
                    '	#hp-content-row-one .ui-article:hover .ui-article-info.has-thumb {' +
					'		background: ' + primaryColor85 + ';' +
                    '}' +
                    '	.gb-footer-contact.phone:before {' +
					'		background: ' + primaryColor35 + ';' +
                    '}' +
                    '	.gb-footer-inner.left:before {' +
					'		background: ' + primaryColor35 + ';' +
                    '}'+
                    '@media (max-width: 767px) {' +
                    	'	#hp-content-row-one-outer:before {' +
						'		background: ' + primaryColor85 + ';' +
                    	'	}' +
                    '}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
        },
        "HexToRgb": function(hex){
            var r = parseInt((this.CutHex(hex)).substring(0,2),16);
            var g = parseInt((this.CutHex(hex)).substring(2,4),16);
            var b = parseInt((this.CutHex(hex)).substring(4,6),16);
            return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
            return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


//TEMPLATE METHODS
        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;
            var template = this;

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search-container");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

            //ANIMATE SEARCH CONTAINER
            $("#gb-search-btn").on("click keydown", function(e) {
            	searchHoverTimer = "";
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    if($("#gb-search-container").hasClass("open")){
                    $(this).attr("aria-expanded","false").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-container").removeClass("open").attr("aria-hidden","true");
                            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

                    } else {
                    	$(this).attr("aria-expanded","true");
                        $("#gb-search-container").addClass("open").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$("#gb-search-container #sw-search-input").focus().attr("tabindex","0");

                        }, 1000);

                    }
                }
            });  

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "Logo": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                   	var logoWidth = $("#gb-logo").width();
                    var border = (logoWidth + 50)/2;
                    $("#gb-logo-bottom").css({
                        "border-left": border + "px solid transparent",
                        "border-right": border + "px solid transparent"
                    }).removeClass("hidden"); 
                break;
                case "768":
                    var logoWidth = $("#gb-logo").width();
                    var border = (logoWidth + 50)/2;
                    $("#gb-logo-bottom").css({
                        "border-left": border + "px solid transparent",
                        "border-right": border + "px solid transparent"
                    }).removeClass("hidden");
                break;
                case "640":
                    var logoWidth = $("#gb-logo").width();
                    var border = (logoWidth + 40)/2;
                    $("#gb-logo-bottom").css({
                        "border-left": border + "px solid transparent",
                        "border-right": border + "px solid transparent"
                    }).removeClass("hidden");
                break;
                case "480":
                    var logoWidth = $("#gb-logo").width();
                    var border = (logoWidth + 32)/2;
                    $("#gb-logo-bottom").css({
                        "border-left": border + "px solid transparent",
                        "border-right": border + "px solid transparent"
                    }).removeClass("hidden");
                break;
                case "320":
                    var logoWidth = $("#gb-logo").width();
                    var border = (logoWidth + 22)/2;
                    $("#gb-logo-bottom").css({
                        "border-left": border + "px solid transparent",
                        "border-right": border + "px solid transparent"
                    }).removeClass("hidden");
                break;
            }
        },

