                  var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                  mmg.props.defaultGallery = false;

                  $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                      "efficientLoad" : true,
                      "imageWidth" : 1500,
                      "imageHeight" : 915,
                      "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                      "galleryOverlay" : false,
                      "linkedElement" : ["image"], // ["image", "title", "overlay"]
                      "playPauseControl" : true,
                      "backNextControls" : true,
                      "bullets" : false,
                      "thumbnails" : false,
                      "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                      "autoRotate" : true,
                      "hoverPause" : true,
                      "transitionType" : "fade", // fade, slide, custom
                      "transitionSpeed" : 1.5,
                      "transitionDelay" : 4,
                      "fullScreenRotator" : true,
                      "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                      "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                      "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                      "onTransitionStart" : function(props) {
                      		$(".mmg-description-outer .mmg-description").wrapInner('
');
                            if($(".mmg-description-outer .mmg-description-title").length){
                                $(".mmg-description-outer .mmg-description-links").insertAfter(".mmg-description-title");
                            }
                            $(".mmg-description-outer .mmg-description-title").next(".mmg-description-links").andSelf().wrapAll('
');
                      }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                      "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                      "allLoaded" : function(props) {
                        $(".mmg-description-outer .mmg-description").wrapInner('
');
                        if($(".mmg-description-outer .mmg-description-title").length){
                            $(".mmg-description-outer .mmg-description-links").insertAfter(".mmg-description-title");
                        }
                        $(".mmg-description-outer .mmg-description-title").next(".mmg-description-links").andSelf().wrapAll('
');


                        if($(".mmg-control.play-pause").length){
                        	$(".mmg-container .mmg-viewer .mmg-control.play-pause").insertBefore($(".mmg-container .mmg-viewer .mmg-control.back"));
                        }
                      }, // props.element, props.mmgRecords
                      "onWindowResize": function(props) {} // props.element, props.mmgRecords
                  });


              }
            }  else {
                $('#sw-content-container10 > div[id*="pmi"]').remove();
            	this.StreamingVideo();
            }
        },

