        "ArticleSlider": function() {
            $("#sw-content-container1.ui-hp .ui-widget.app.headlines").csArticleSlider({
                "viewerNum": [3, 2, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
