            });
            $("#sw-content-container1.ui-hp .ui-widget.app.upcomingevents").csArticleSlider({
                "viewerNum": [5, 3, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            //MOVE THE ARROW CONTROLS AND THE VIEW ALL TO THE HEADER
            $(".hp.column.one div.ui-widget.app").each(function(appIndex, app){
                if($(window).width() >= 768){
                    if( $('.view-calendar-link', app).length > 0 ){
                        $('.view-calendar-link', app).appendTo( $('.ui-widget-header', app) );
                    } 
                    if( $('.more-link', app).length > 0 ){
                        $('.more-link', app).appendTo( $('.ui-widget-header', app) );
                    } 

                    $(".ui-widget-header a", app).wrapAll('');
                } else {
					if( $('.view-calendar-link', app).length > 0 ){
                        $('.view-calendar-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                    if( $('.more-link', app).length > 0 ){
                        $('.more-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                }      
            });

            //WRAP HEADLINES TITLE AND TEASER TEXT
            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines .ui-article-title").each(function() {
                $(this).next(".ui-article-description").andSelf().wrapAll('
');

            });

            $("#hp-content-row-one .ui-article-info.has-thumb .ui-article-title").each(function(){
            	$("> a",this).focus(function(){
            	$(this).parent().css("top","0px");
            	});
            });

            $(".hp.column.two div.ui-widget.app").each(function(appIndex, app){
                if($(window).width() < 1024){
					if( $('.view-calendar-link', app).length > 0 ){
                        $('.view-calendar-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                    if( $('.more-link', app).length > 0 ){
                        $('.more-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                }      
            });

            $(".hp.column.three div.ui-widget.app").each(function(appIndex, app){
                if($(window).width() > 768){
                    if( $('.view-calendar-link', app).length > 0 ){
                        $('.view-calendar-link', app).appendTo( $('.ui-widget-header', app) );
                    } 
                    if( $('.more-link', app).length > 0 ){
                        $('.more-link', app).appendTo( $('.ui-widget-header', app) );
                    } 

                    $(".ui-widget-header a", app).wrapAll('');
                } else {
					if( $('.view-calendar-link', app).length > 0 ){
                        $('.view-calendar-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                    if( $('.more-link', app).length > 0 ){
                        $('.more-link', app).prependTo( $('.ui-widget-detail', app) );
                    } 
                }

            });

            $("#sw-content-container1.ui-hp .ui-widget.app.headlines .ui-article").each(function() {
            	$(this).attr("tabindex", "0");
            });

            // ADD/REMOVE HOVER CLASS FOR HOVER/FOCUS/TOUCH
			$(document).on("mouseenter focusin", "#hp-content-row-one .ui-article", function(e) {
            	$(this).addClass("hover");
            }).on("mouseleave focusout", "#hp-content-row-one .ui-article", function(e) {
