function initFooterCurve() {

  var canvas = document.getElementById("footer-curve");
  var ctx = canvas.getContext("2d");

  drawFooterCurve(ctx);
}

function drawFooterCurve(ctx) {

  var alpha = ctx.globalAlpha;

  // layer1/Path
  ctx.save();
  ctx.globalAlpha = alpha * 0.24;
  ctx.beginPath();
  ctx.moveTo(151.2, 210.7);
  ctx.bezierCurveTo(413.8, 182.7, 685.6, 110.8, 961.2, 62.2);
  ctx.bezierCurveTo(1104.6, 36.9, 1249.4, 18.3, 1394.0, 6.5);
  ctx.lineTo(32.3, 6.5);
  ctx.lineTo(32.3, 0.0);
  ctx.lineTo(27.7, 0.0);
  ctx.lineTo(0.0, 217.2);
  ctx.bezierCurveTo(50.2, 218.2, 101.0, 216.0, 151.2, 210.7);
  ctx.closePath();
  ctx.fillStyle = "rgb(255, 255, 255)";
  ctx.fill();
  ctx.restore();
}

function initGICurve() {

  var canvas = document.getElementById("global-icon-curve");
  var ctx = canvas.getContext("2d");

  drawGICurve(ctx);
}

function drawGICurve(ctx) {

  var alpha = ctx.globalAlpha;

  // layer1/Path
  ctx.save();
  ctx.globalAlpha = alpha * 0.24;
  ctx.beginPath();
  ctx.moveTo(0.0, 0.0);
  ctx.lineTo(0.0, 92.8);
  ctx.lineTo(220.0, 92.8);
  ctx.bezierCurveTo(385.7, 66.6, 554.8, 29.7, 725.7, 0.0);
  ctx.lineTo(0.0, 0.0);
  ctx.closePath();
  ctx.fillStyle = "rgb(255, 255, 255)";
  ctx.fill();
  ctx.restore();

  }

