    function InsertRating4017071() {
    var ModuleInstanceID = 40;
    var FlexDataID = 17071;
    var Rating = $('#swmoduleinstancerating-hid-rating4017071').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating4017071();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.wacoisd.org//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating4017071']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating4017071Values();
                InsertRating4017071($('#swmoduleinstancerating-hid-rating4017071').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating4017071Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating4017071']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating4017071').val((i + 1) / 2);
            }
        });
    }

