	gaplusu( 'create', 'UA-22802867-1', 'auto' ); // Create network tracking.
	gaplusu( 'send', 'pageview' ); // Send pageview.
	gaplusu( 'create', 'UA-54735145-1', 'auto', { 'name': 'single' } ); // Create single site tracking.
	gaplusu( 'single.send', 'pageview' ); // Send pageview.
}

