        (function ($) {

					$(".read-more-trigger").on("click", function(e){
						e.preventDefault();
						if(!$(this).hasClass('active')) {
							$('.readMoreContainer').slideDown('fast');
							$(this).text('Read Less >').addClass('active');
						} else {
							$('.readMoreContainer').slideUp('fast');
							$(this).text('Read More >').removeClass('active');
						}
					});

