	function pgScreenSize(){
		var getWidth = $(window).width();

		multimediaRotator();
		$('#hp-school-header').css({
			top: $('header').outerHeight() + 20, /* Changed from 35 by Jeremy - 10-19-15 */
			left: 'calc( 50% - ' + ($('#hp-school-header').outerWidth()/2) + 'px )'
		});

		if (getWidth >= 1024) { //Desktop View
			if( $('.hp').length > 0 ){
				//adjust placement of schoolname over gallery
				$('#hp-school-header').css({
					'top':  $('#gb-page > header').outerHeight() + 40,  /* Changed from 80 by Jeremy - 10-19-15 */
					'left': 'calc(50% - ' + ($('#hp-school-header').outerWidth()/2) + 'px)'
				});
			}
			positionPopularLinks();
		}else{ //Mobile BPs
			if(getWidth < 640){
				appView();
			}

			$('#gb-popular-links > ul').css('top', $('header').outerHeight());
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 1500,
		                "imageHeight"             : 915,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "no",
		                "captions"                : "no",
		                "galleryOverlay"          : "yes",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "off",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960", "768"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
		                	if( $("#hp-slideshow .rs-gallery-control").length > 0 ){
						$("#hp-slideshow .rs-gallery-control.back").addClass('icon-font icon-thin-arrow point-left rotate');
						$("#hp-slideshow .rs-gallery-control.next").addClass('icon-font icon-thin-arrow');
						$('#hp-slideshow .rs-gallery-images').after('
');
						$("#hp-slideshow .rs-gallery-control").appendTo($('#hp-slideshow .control-cont'));
					}
		                }
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "no",
	            monthLong   : "yes",
	            dayWeek     : "no"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	        if( $('#gb-page').hasClass('hp') ) customizeUpcomingEvents();
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });

	    $(".ui-article", container).each(function(){
	   	 $('.ui-article-description', this).each(function(){
			if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
				$(this).prepend('ALL DAY');
			}
		});
	     });
	}

	function customizeUpcomingEvents(){
		$('.hp.row.two .upcomingevents.app').each(function(k, v){
			var newEvents = '';
			var existingMonths = {};
			var calendarLink = $('.view-calendar-link', v).attr('href');

			$(".ui-article", v).each(function(){
				var month = $(".joel-month", this).text();
				$(".joel-month", this).remove(); //get rid of the month so we can just use the date

				var dayNum = $(".sw-calendar-block-date", this).html().trim(); //grabs the number of the month
				var dayInfo = $(".ui-article-description", this).html(); //grabs all of the events for the day
				dayInfo = '
' + dayInfo + 'View All'; //make it look normal again

				if(dayNum.length == 1) dayNum = "0" + dayNum;

				if( month in existingMonths ){ // just append the data to the array pos
					existingMonths[month][dayNum] = dayInfo; //push up new data
				}else{ //Add the array position and then add the data.
					existingMonths[month] = {}; //we store the data in an array
					existingMonths[month][dayNum] = dayInfo; //add the data to the array
				}
			});

			$.each(existingMonths, function(k1, v1){
				var eventsHtml = '
' + k1 + '';

				$.each(existingMonths[k1], function(k2, v2){
					eventsHtml += '
' + k2 + ' ' + v2 + '';
				});

				newEvents += eventsHtml += '';
			});
			$('.view-calendar-link', v).appendTo($('.ui-widget-footer', this));
			$('.ui-widget-detail', v).html(newEvents);
		});
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 span.accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('+');
				}
			}
		});

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click').click(function(){
			 if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
		                $(this).parent().removeClass("active");
		            }else{
		                var clickedApp = $(this).parent().parent().attr("id");
		                $("#hp-content-inner div.ui-widget.app.active").removeClass("active"); 
		                $(this).parent().addClass("active");
		                smoothScroll("#" + clickedApp, 3);
		            }
		});
	}	

