	function eventListeners(){
		/* Add button event listeners for popular links */
		$('.global-icon-trigger').click(function(e){
			if( !$(this).hasClass('open') ){
				$('#gb-popular-links, .global-icon-trigger').removeClass('closed').addClass('open');
			}else{
				$('#gb-popular-links, .global-icon-trigger').removeClass('open').addClass('closed');
			}
		});

		/* Add event listeners for mystart bar */
		$('.gb-mystart-drop-trigger').click(function(e){
			$('#gb-custom-mystart-outer').slideToggle();
			if( !$(this).hasClass('open') ){
				$('.gb-mystart-drop-trigger').removeClass('closed').addClass('open');
			}else{
				$('.gb-mystart-drop-trigger').removeClass('open').addClass('closed');
			}
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	/* customDropdowns
	**  Use: Sets up custom dropdown menus.
	**  Param: openIcon, closedIcon -- The classes that you want to mark the on/off state of the menu icons (dont pass the . or #)
	*/
	function customDropdowns(openIcon, closedIcon){
		$('#gb-custom-mystart .custom-dropdown').first().addClass('last-menu-item');
		//Custom Dropdown Functionality
		$('.custom-dropdown').click(function(e){
		    if( $(this).hasClass("openMenu") ){
		    	$('.dropdown', this).slideUp();
		        $(this).removeClass("openMenu");
		        $("> p span", this).removeClass(openIcon).addClass(closedIcon);
		    }else{
		    	$('.custom-dropdown.openMenu .dropdown', this).slideUp();
		    	$('.custom-dropdown.openMenu > p span').removeClass(openIcon).addClass(closedIcon);
		        $('.custom-dropdown.openMenu').removeClass('openMenu');
		        $('.dropdown', this).slideDown();
		        $(this).addClass("openMenu");
		        $('> p span', this).removeClass(closedIcon).addClass(openIcon);
		    }
		})
		.hover(function(e){},function(e){ //Out
		    $('.dropdown', this).slideUp();
		    $(this).removeClass("openMenu");
		    $('> p span', this).removeClass(openIcon).addClass(closedIcon);
		});
	}

	/*
	** setupBullets
	** Use: Classes all bullets so we can manipulate their CSS and apply our font-icons
	*/
	function setupBullets(){
		$('.ui-widget.app.navigation').each(function(k, v){  //go through each app
 			$('.ui-widget-detail ul li', this).each(function(k1, v1){ // go through every li
				$('.bullet', this).addClass('icon-font');
			});
		});
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the id that was passed to the function.
	** Return: N/A
	** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
	** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
	*/
	function smoothScroll( to, offset ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, 800, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}

	function megaMenu(){
		if( "Manson" == districtAlias ){
			//build out the menus

			var megaColumns = [ //list of backend data split by channels
				[	//Channel 1 ( [how many items can be present], [label for that column] last array entry is max of each columns count )
					[5, "District Information"],
					[5, "District Departments"],
					[5, "Meet the Staff"],
					[5, "Communications"],
					5 + 5 + 5 + 5
				],
				[	//Channel 2
					[4, "District Roadmap"],
					[4, "Parent Info"],
					[4, "Academics"],
					[4, "Communications"],
					4 + 4 + 4 + 4
				],
				[	//Channel 3
					[4, "District Roadmap"],
					[4, "Student Information"],
					[4, "Academics"],
					[4, "Communications"],
					4 + 4 + 4 + 4
				],
				[	//Channel 4
					[4, "Staff Information"],
					[4, "Popular Links"],
					[4, "Communications"],
					[4, "Staff Resources"],
					4 + 4 + 4 + 4
				]
			];

			var channelCount = 0;

			$('#channel-navigation > li').each(function(k, v){
				if( $('.sw-channel-dropdown li', v).length > 0 ){
					if( channelCount < 4 ){ //if we havent hit the limit that our backend data can support keep going
						var columnCount = 1;
						var columnItems = 0;

						var newListHeaders = '

' + megaColumns[channelCount][0][1] + ''; //add the first column header
						var newList = '
'; //add the first ul

						$('.sw-channel-dropdown > li', v).each(function(k1, v1){ //go through each item
							if( k1 < megaColumns[channelCount][4] ){	//if we havent exceeded the client set max item limit keep adding
								if( columnItems == megaColumns[channelCount][columnCount][0] ){ //will check when we have reached the end of a column
									newListHeaders += '
' + megaColumns[channelCount][columnCount][1] + '';
									newList += '';
									columnCount++; //mark the new column
									columnItems = 1; //reset it to 1 because we will add the link this round
								}else{
									columnItems++; //keep track of how many items we have added to this column
								}
								newList += '
' + $(v1).html() + ''; //actually add the item to the list
							}else{
								return true; //break - we have met the client set max limit of items, so we will stop looping.
							}
						});

						newList =  '
' + newListHeaders + '
' + newList + ''; //close the dropdown div and inner div
						$('.sw-channel-dropdown', v).remove(); //remove the original
						$(v).append(newList).addClass('mega-menu-cont'); //append our new one
						$('.sw-channel-dropdown', v).hide(); //now hide it.

						channelCount++; //set the next channel count if there is one.

						//add arrow like a badge!
						$('> a span', v).append('');
					}else{
						return true; //break - we have exceeded the limit our backend data can support
					}
				}
			});
		}

		//add icons 
	}

	function sameHeights(target){
		var maxH = 0;

		$(target).css('height', 'auto');
		$(target).each(function(k, v){
			if( $(this).outerHeight() > maxH ) maxH = $(this).outerHeight();
		});
		$(target).css('height', maxH);
	}
	function headlineCards(){
		if( $('.hp').length > 0 ){
			$('.hp.row.two .headlines').each(function(k, v){
				$('.ui-articles > li', this).each(function(k1, v1){
					$('.ui-article-thumb .img', this).append('

');

					$('.ui-article-title', this).appendTo($('.card-info > div', this));

					$('.ui-article-description', this).appendTo($('.card-info > div', this));

					$('.custom-read-more', this).hide();
				});
			});	
		}
	}

	function positionPopularLinks(){
		//some simple math to keep the popular links UL in the middle of the viewport vertically
		var halfWindowH = ($(window).height() + $('#gb-page > header').outerHeight() - $('#gb-popular-links ul').outerHeight())/2;
		$('#gb-popular-links ul').stop().animate({'top': halfWindowH}, 350);
	}

	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/

	function stickyHeader(shutoff){
		var getWidth = $(window).width();
		if( getWidth >= shutoff){
			var top = $(window).scrollTop();
				//check where the window is
			if(top >= scrollHookOn  && !$('nav').hasClass('on')){ //make it sticky
				$('#sticky-header-ghost').addClass('on').css('height', $('header').outerHeight(true));
				$('nav, header').addClass('on');

			}else if( top <= scrollHookOff  && $('nav').hasClass('on')){ //unstick it
				$('nav, #sticky-header-ghost, header').removeClass('on');
			}

			if(!$('nav').hasClass('on')){
				$('.mega-menu-dropdown').css('top', $('header').outerHeight() - top);

			}else{
				$('.mega-menu-dropdown').css('top', 'auto');

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                    }
                }
			}

			positionPopularLinks();
		}else if( $('nav').hasClass('on') ){
			$('nav, #sticky-header-ghost, header').removeClass('on');
		}
	}

