		slideshowTimer_489610 = setTimeout("switchDip_489610(" + num + ")",time);
	}
	
	function dipNavigationDelay_489610(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_489610();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_489610 == 1){
				dipNavigation_489610(nums,speed);
			}else{
				if(typeof dipNavTime_489610 !== "undefined"){
					clearTimeout(dipNavTime_489610);
				}
				
				dipNavTime_489610 = setTimeout(function(){dipNavigationDelay_489610(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_489610(nums,speed){
		clearTimeout(slideshowTimer_489610);
		
		/*if(typeof slideshowJump_489610 !== "undefined"){
			clearTimeout(slideshowJump_489610);
		}*/
		dipNavTimeVar_489610 = 0;
		var slideshowMotion = slideshowMotion_489610();
		var slideshowMotionSpeed = slideshowMotionSpeed_489610();
		var slideshowQuickNavSpeed = slideshowQuickNavSpeed_489610();
		var complete = document.getElementById('complete_489610').value;
		var currentNum = document.getElementById('currentNum_489610').value;
		var time = document.getElementById('slideshowSpeed_489610').value;
		var totalDips = document.getElementById('totalDips_489610').value;
		var direction = directions_489610(slideshowMotion);
