			}

			if($('#videoShell_489610' + currentNum).length){
					closePlayer_489610(currentNum);
					$('#videoShell_489610' + currentNum).fadeOut(speed);
				}
			if($('#dipContent_489610' + currentNum).length){
				$('#dipContent_489610' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_489610' + currentNum).length){
				$('#nav_489610' + (nums)).addClass('on');
			}
			if($('#nav_li_489610' + currentNum).length){
				$('#nav_li_489610' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_489610' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_489610').value = 1;
					dipNavTimeVar_489610 = 1;
				});
			}else{
				$('#dipImage_489610' + (nums)).fadeIn(speed);
				dipNavTimeVar_489610 = 1;
			}
			if($('#videoShell_489610' + nums).length){
					$('#videoShell_489610' + nums).fadeIn(speed);
				}
			if($('#dipContent_489610' + nums).length){
