	function switchDip_489610(num,pause){
		clearTimeout(slideshowTimer_489610);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_489610();
		var speed = slideshowMotionSpeed_489610();
		var currentNum = document.getElementById('currentNum_489610').value;
		var totalDips = document.getElementById('totalDips_489610').value;
		var video = document.getElementById('video_489610').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_489610(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_489610' + nums).length){
				$('#nav_489610' + nums).removeClass('on');
			}
			
			if($('#nav_li_489610' + nums).length){
				$('#nav_li_489610' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_489610' + nums).length){
					$('#videoShell_489610' + nums).hide();
				}
				$('#dipImage_489610' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_489610' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_489610' + nums).length){
				$('#videoShell_489610' + nums).fadeOut(speed);
			}
			if($('#dipContent_489610' + nums).length){
				$('#dipContent_489610' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_489610' + nums).length){
				$('#nav_489610' + nums).addClass('on');
			}
			
			if($('#nav_li_489610' + nums).length){
				$('#nav_li_489610' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_489610' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_489610' + nums).length){
					$('#videoShell_489610' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_489610' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_489610' + nums).length){
					$('#videoShell_489610' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_489610' + nums).length){
				$('#dipContent_489610' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_489610(timer);
			document.getElementById('currentNum_489610').value = nums;
		}
	}
	
	function slideshowMotion_489610(){
		if($('#slideshowMotion_489610').length){
			slideshowMotion = document.getElementById('slideshowMotion_489610').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_489610(){
		if($('#slideshowMotionSpeed_489610').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_489610').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_489610(){
		if($('#slideshowQuickNavSpeed_489610').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_489610').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_489610(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

