	}
	
	function closePlayer_489610(num){
		$('#videoPlayer_489610' + num).fadeOut(200);
		$('#video_489610').val(0);
		var video = $('#VideoPlayback_489610' + num).attr("src");
		$('#VideoPlayback_489610' + num).attr("src","");
		$('#VideoPlayback_489610' + num).attr("src",video);
		reRunSlideshow_489610();
	}
	
	function getVideoiframeSrc_489610(num){
		var iframeSrc;
		if($('.Videoiframe_489610' + num).length){
			iframeSrc = $('.Videoiframe_489610' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_489610' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_489610' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

