	function switchDip_480868(num,pause){
		clearTimeout(slideshowTimer_480868);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_480868();
		var speed = slideshowMotionSpeed_480868();
		var currentNum = document.getElementById('currentNum_480868').value;
		var totalDips = document.getElementById('totalDips_480868').value;
		var video = document.getElementById('video_480868').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_480868(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_480868' + nums).length){
				$('#nav_480868' + nums).removeClass('on');
			}
			
			if($('#nav_li_480868' + nums).length){
				$('#nav_li_480868' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_480868' + nums).length){
					$('#videoShell_480868' + nums).hide();
				}
				$('#dipImage_480868' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_480868' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_480868' + nums).length){
				$('#videoShell_480868' + nums).fadeOut(speed);
			}
			if($('#dipContent_480868' + nums).length){
				$('#dipContent_480868' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_480868' + nums).length){
				$('#nav_480868' + nums).addClass('on');
			}
			
			if($('#nav_li_480868' + nums).length){
				$('#nav_li_480868' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_480868' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_480868' + nums).length){
					$('#videoShell_480868' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_480868' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_480868' + nums).length){
					$('#videoShell_480868' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_480868' + nums).length){
				$('#dipContent_480868' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_480868(timer);
			document.getElementById('currentNum_480868').value = nums;
		}
	}
	
	function slideshowMotion_480868(){
		if($('#slideshowMotion_480868').length){
			slideshowMotion = document.getElementById('slideshowMotion_480868').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_480868(){
		if($('#slideshowMotionSpeed_480868').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_480868').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_480868(){
		if($('#slideshowQuickNavSpeed_480868').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_480868').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_480868(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

