	}
	
	function getVideoiframeSrc_480868(num){
		var iframeSrc;
		if($('.Videoiframe_480868' + num).length){
			iframeSrc = $('.Videoiframe_480868' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_480868' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_480868' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

    
