link.script:
$(document).ready(function() {
	if ($('.white-popup').length) {
		$('.slide-pop').magnificPopup({
		  disableOn: 700,
		  type: 'inline',
		  midClick: true,
		  mainClass: 'mfp-fade',
		  removalDelay: 160,
		  preloader: false,
		  fixedContentPos: false
		});
	}
	$('.main-slider').slick({
	  dots:  false,
	  arrows:true,
	  prevArrow: 'Previous Slide',
	  nextArrow: 'Next Slide',
	  fade: true,
	  infinite: true,
	  adaptiveHeight: true, 
	  autoplay: true,
	  autoplaySpeed: '4000'
	});
});
