	function switchDip_443415(num,pause){
		clearTimeout(slideshowTimer_443415);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_443415();
		var speed = slideshowMotionSpeed_443415();
		var currentNum = document.getElementById('currentNum_443415').value;
		var totalDips = document.getElementById('totalDips_443415').value;
		var video = document.getElementById('video_443415').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_443415(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_443415' + nums).length){
				$('#nav_443415' + nums).removeClass('on');
			}
			
			if($('#nav_li_443415' + nums).length){
				$('#nav_li_443415' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_443415' + nums).length){
					$('#videoShell_443415' + nums).hide();
				}
				$('#dipImage_443415' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_443415' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_443415' + nums).length){
				$('#videoShell_443415' + nums).fadeOut(speed);
			}
			if($('#dipContent_443415' + nums).length){
				$('#dipContent_443415' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_443415' + nums).length){
				$('#nav_443415' + nums).addClass('on');
			}
			
			if($('#nav_li_443415' + nums).length){
				$('#nav_li_443415' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_443415' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_443415' + nums).length){
					$('#videoShell_443415' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_443415' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_443415' + nums).length){
					$('#videoShell_443415' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_443415' + nums).length){
				$('#dipContent_443415' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_443415(timer);
			document.getElementById('currentNum_443415').value = nums;
		}
	}
	
	function slideshowMotion_443415(){
		if($('#slideshowMotion_443415').length){
			slideshowMotion = document.getElementById('slideshowMotion_443415').value;
		}else{
