				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_410704 == 1){
				dipNavigation_410704(nums,speed);
			}else{
				if(typeof dipNavTime_410704 !== "undefined"){
					clearTimeout(dipNavTime_410704);
				}
				
				dipNavTime_410704 = setTimeout(function(){dipNavigationDelay_410704(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_410704(nums,speed){
		clearTimeout(slideshowTimer_410704);
		
