			if($('#dipContent_410704' + currentNum).length){
				$('#dipContent_410704' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_410704' + currentNum).length){
				$('#nav_410704' + (nums)).addClass('on');
			}
			if($('#nav_li_410704' + currentNum).length){
				$('#nav_li_410704' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_410704' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_410704').value = 1;
					dipNavTimeVar_410704 = 1;
				});
			}else{
				$('#dipImage_410704' + (nums)).fadeIn(speed);
				dipNavTimeVar_410704 = 1;
			}
