	function switchDip_410704(num,pause){
		clearTimeout(slideshowTimer_410704);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_410704();
		var speed = slideshowMotionSpeed_410704();
		var currentNum = document.getElementById('currentNum_410704').value;
		var totalDips = document.getElementById('totalDips_410704').value;
		var video = document.getElementById('video_410704').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_410704(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_410704' + nums).length){
				$('#nav_410704' + nums).removeClass('on');
			}
			
			if($('#nav_li_410704' + nums).length){
				$('#nav_li_410704' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_410704' + nums).length){
					$('#videoShell_410704' + nums).hide();
				}
				$('#dipImage_410704' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_410704' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_410704' + nums).length){
				$('#videoShell_410704' + nums).fadeOut(speed);
			}
			if($('#dipContent_410704' + nums).length){
				$('#dipContent_410704' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_410704' + nums).length){
				$('#nav_410704' + nums).addClass('on');
			}
			
			if($('#nav_li_410704' + nums).length){
				$('#nav_li_410704' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_410704' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_410704' + nums).length){
					$('#videoShell_410704' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_410704' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_410704' + nums).length){
					$('#videoShell_410704' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_410704' + nums).length){
				$('#dipContent_410704' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_410704(timer);
			document.getElementById('currentNum_410704').value = nums;
		}
	}
	
	function slideshowMotion_410704(){
		if($('#slideshowMotion_410704').length){
			slideshowMotion = document.getElementById('slideshowMotion_410704').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_410704(){
		if($('#slideshowMotionSpeed_410704').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_410704').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_410704(){
		if($('#slideshowQuickNavSpeed_410704').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_410704').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_410704(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

