	}
	
	function closePlayer_410704(num){
		$('#videoPlayer_410704' + num).fadeOut(200);
		$('#video_410704').val(0);
		var video = $('#VideoPlayback_410704' + num).attr("src");
		$('#VideoPlayback_410704' + num).attr("src","");
		$('#VideoPlayback_410704' + num).attr("src",video);
		reRunSlideshow_410704();
	}
	
	function getVideoiframeSrc_410704(num){
		var iframeSrc;
		if($('.Videoiframe_410704' + num).length){
			iframeSrc = $('.Videoiframe_410704' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_410704' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_410704' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

    
