	function googleTranslateElementInit() {
			new google.translate.TranslateElement({
			
			pageLanguage: 'en',
			//layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
			autoDisplay: false
		}, 'google_translate_element');
		$('.goog-te-gadget-simple').css('font-size', '10px');
		$('.goog-te-menu-frame').attr('title', 'Google Translator');
		// begin accessibility compliance, requires jQuery
        $('img.goog-te-gadget-icon').attr('alt','Google Translate');
        $('div#goog-gt-tt div.logo img').attr('alt','translate');
        $('div#goog-gt-tt .original-text').css('text-align','left');
        $('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
		$('.goog-te-combo').attr('title','Select A Language');
		//$("html").attr("lang", pageLanguage);
        // end accessibility compliance
	}

