$(document).ready(function() {
	homePageFunctions();
	if ($('.white-popup').length) {
		$('.slide-pop').magnificPopup({
		  disableOn: 700,
		  type: 'inline',
		  midClick: true,
		  mainClass: 'mfp-fade',
		  removalDelay: 160,
		  preloader: false,
		  fixedContentPos: false
		});
	}
});

    (function($) {
      $.fn.clickToggle = function(func1, func2) {
        var funcs = [func1, func2];
        this.data('toggleclicked', 0);
        this.click(function() {
          var data = $(this).data();
          var tc = data.toggleclicked;
          $.proxy(funcs[tc], this)();
          data.toggleclicked = (tc + 1) % 2;
        });
        return this;
      };
    }(jQuery));
    
    $('a.slidePlay').clickToggle(function() {
      $('.main-slider').slick('slickPlay');
    }, function() {
      $('.main-slider').slick('slickPause');
    });
	
    $(document).ready(function() {
		$('.main-slider').slick({
			dots: false,
			fade: true,
			infinite: true,
			adaptiveHeight: true, 
			autoplay: true,
			autoplaySpeed: '4000',
			arrows: true,
			appendArrows: '.slideNav',
			prevArrow:'Previous Slide',
			nextArrow:'Next Slide'
		});

		$('a.slidePlay').click();
		
