	function switchDip_472212(num,pause){
		clearTimeout(slideshowTimer_472212);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_472212();
		var speed = slideshowMotionSpeed_472212();
		var currentNum = document.getElementById('currentNum_472212').value;
		var totalDips = document.getElementById('totalDips_472212').value;
		var video = document.getElementById('video_472212').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_472212(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_472212' + nums).length){
				$('#nav_472212' + nums).removeClass('on');
			}
			
			if($('#nav_li_472212' + nums).length){
				$('#nav_li_472212' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_472212' + nums).length){
					$('#videoShell_472212' + nums).hide();
				}
				$('#dipImage_472212' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_472212' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_472212' + nums).length){
				$('#videoShell_472212' + nums).fadeOut(speed);
			}
			if($('#dipContent_472212' + nums).length){
				$('#dipContent_472212' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_472212' + nums).length){
				$('#nav_472212' + nums).addClass('on');
			}
			
			if($('#nav_li_472212' + nums).length){
				$('#nav_li_472212' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_472212' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_472212' + nums).length){
					$('#videoShell_472212' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_472212' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_472212' + nums).length){
					$('#videoShell_472212' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_472212' + nums).length){
				$('#dipContent_472212' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_472212(timer);
			document.getElementById('currentNum_472212').value = nums;
		}
	}
	
	function slideshowMotion_472212(){
		if($('#slideshowMotion_472212').length){
			slideshowMotion = document.getElementById('slideshowMotion_472212').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
