$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=TO%2fKd570c9Va30r%2bOyHnOw%3d%3d&SessionID=e55ab56e-7f91-43f9-bcc0-10b36a619769&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+12%3a17%3a55+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.67.19';
